pragma solidity >=0.4.22 <0.7.0;
pragma experimental ABIEncoderV2;

interface TokenToBounty {
    function transfer(address _to, uint256 _value) external returns (bool success);
}

interface TokenProxyToBounty {
    function transferFromProxy(address recipient, uint256 amount) external returns (bool);
}

contract Bounty {
    TokenToBounty token;
    TokenProxyToBounty tokenproxy;

    constructor(address elt, address proxy) public {
        token = TokenToBounty(elt);
        tokenproxy = TokenProxyToBounty(proxy);
    }

     /*
    金主发布悬赏   Created 
    金主取消任务   Cancelled
    金主确认猎人   Confirmed
    猎人A提交任务  HunterFinished
    猎人A超时未提交任务 HunterOutOfTime 
    金主确认任务完成  Promoterfinished
    金主超时未确认任务  PromoterOutOfTime
    */
    enum State { Created, Cancelled, Confirmed,  HunterFinished, HunterOutOfTime, Promoterfinished, PromoterOutOfTime}

    uint24 public id = 0;
    uint8 public fee = 5;
    address feeTo = 0x9fc10900cF34636330D05e9c6CA976b918627023;
    struct TaskMessage 
    {
        string hash;
        address promoter;
        address hunterA;
        address[] hunters;
        string[] reasonHashs;
        uint bonus;
        uint taskStart;
        uint taskDeadLine;
        State state;
    }

    mapping(uint24 => TaskMessage) public tasks;


    modifier onlyPromoter(uint24 _uid) {
        require(msg.sender == tasks[_uid].promoter);
        _;
    }

    modifier onlyhunterA(uint24 _uid) {
        require(msg.sender == tasks[_uid].hunterA);
        _;
    }

    modifier inState(uint24 _uid, State _state) {
        require(tasks[_uid].state == _state );
        _;
    }

    modifier inTime(uint24 _uid) {
        require(now <= tasks[_uid].taskStart + tasks[_uid].taskDeadLine);
        _;
    }

    modifier outOfTime(uint24 _uid) {
        require(now > tasks[_uid].taskStart + tasks[_uid].taskDeadLine);
        _;
    }

    //事件日志
    event SetTask(uint24 indexed uid, address indexed promoter, TaskMessage itask);
    event ApplyTask(uint24 indexed uid, address indexed hunter, string hash);
    event CancelTask(uint24 indexed uid, address indexed promoter, string hash);
    event ConfirmHunter(uint24 indexed uid, address indexed promoter, address indexed hunterA, string hash);
    event HunterAFinished(uint24 indexed uid, address indexed hunterA, string hash);
    event HunterAOutOfTime(uint24 indexed uid, address indexed promoter, string hash);
    event PromoterFinished(uint24 indexed uid, address indexed promoter, string hash);
    event TaskFallback(uint24 indexed uid, address indexed promoter, string hash);
    event PromoterOutOfTime(uint24 indexed uid, address indexed hunterA, string hash);


    //金主发布悬赏。
    function setTask(string memory _hash, uint _bonus, uint _hour, uint _minunt) 
        public 
    {       
        id += 1;
        tasks[id].hash = _hash;
        tasks[id].promoter = msg.sender;
        tasks[id].bonus = _bonus;
        tasks[id].taskDeadLine = _hour * 1 hours + _minunt * 1 minutes;
        tokenproxy.transferFromProxy(address(this), _bonus+_bonus/2);
        emit SetTask(id, msg.sender, tasks[id]);
    }

    //猎人申请任务
    function applyTask(uint24 _uid, string memory _reasonHashs) 
        public
        inState(_uid, State.Created)
    {   
        require(tasks[_uid].promoter != msg.sender);
        tasks[_uid].hunters.push(msg.sender);
        tasks[_uid].reasonHashs.push(_reasonHashs);
        emit ApplyTask(_uid, msg.sender, tasks[_uid].hash);
    }

    //金主取消任务
    function cancelTask(uint24 _uid)
        public
        inState(_uid, State.Created)
        onlyPromoter(_uid)
    {
        token.transfer(msg.sender, tasks[_uid].bonus+tasks[_uid].bonus/2);
        tasks[_uid].state = State.Cancelled;
        emit CancelTask(_uid, msg.sender, tasks[_uid].hash);
    }

    //金主确认猎人A
    function confirmHunter(uint24 _uid, address a)
        public
        inState(_uid, State.Created)
        onlyPromoter(_uid)
    {
        tasks[_uid].hunterA = a;
        tasks[_uid].taskStart = now;
        tasks[_uid].state = State.Confirmed;
        emit ConfirmHunter(_uid, msg.sender, a, tasks[_uid].hash);
    }

    //猎人A提交任务
    function hunterAFinished(uint24 _uid)
        public
        inTime(_uid)
        inState(_uid, State.Confirmed)
        onlyhunterA(_uid)
    {
        tasks[_uid].taskStart = now;
        tasks[_uid].state = State.HunterFinished;
        emit HunterAFinished(_uid, msg.sender, tasks[_uid].hash);
    }

    //猎人A超时未提交任务,金主取回赏金和押金
    function hunterAOutOfTime(uint24 _uid)
        public
        outOfTime(_uid)
        onlyPromoter(_uid)
    {
        uint feeAmount = tasks[_uid].bonus * 5 / 1000;
        token.transfer(msg.sender, tasks[_uid].bonus+tasks[_uid].bonus/2-feeAmount);
        token.transfer(feeTo, feeAmount);
        tasks[_uid].state = State.HunterOutOfTime;
        emit HunterAOutOfTime(_uid, msg.sender, tasks[_uid].hash);
    }

    //金主确认任务完成
    function promoterFinished(uint24 _uid)
        public
        inTime(_uid)
        inState(_uid, State.HunterFinished)
        onlyPromoter(_uid)
    {
        uint feeAmount = tasks[_uid].bonus * 5 / 1000;
        token.transfer(msg.sender, tasks[_uid].bonus/2 - feeAmount);
        token.transfer(feeTo, feeAmount);
        token.transfer(tasks[_uid].hunterA, tasks[_uid].bonus);
        tasks[_uid].state = State.Promoterfinished;
        emit PromoterFinished(_uid, msg.sender, tasks[_uid].hash);
    }

    //金主退回任务
    function taskFallback(uint24 _uid)
        public
        inTime(_uid)
        inState(_uid, State.HunterFinished)
        onlyPromoter(_uid)
    {
        tasks[_uid].taskStart = now;
        tasks[_uid].state = State.Confirmed;
        emit TaskFallback(_uid, msg.sender, tasks[_uid].hash);
    }

    //金主超时未确认任务，猎人取出赏金，金主押金被罚没
    function promoterOutOfTime(uint24 _uid)
        public
        outOfTime(_uid)
        inState(_uid, State.HunterFinished)
        onlyhunterA(_uid)
    {
        uint feeAmount = tasks[_uid].bonus * 5 / 1000;
        token.transfer(feeTo, feeAmount);
        token.transfer(msg.sender, tasks[_uid].bonus);
        tasks[_uid].state = State.PromoterOutOfTime;
        emit PromoterOutOfTime(_uid, msg.sender, tasks[_uid].hash);
    }

    //获取任务信息
    function getTask(uint24 _uid) 
        public 
        view
        returns (TaskMessage memory) 
    {
        return (tasks[_uid]);
    }

}
