pragma solidity >=0.4.22 <0.7.0;
pragma experimental ABIEncoderV2;

interface TokenToPurchase {
    function transferFromOrigin(address _to, uint256 _value) external returns (bool success);
}

contract Purchase {
    TokenToPurchase token = TokenToPurchase(0x241379715A7aB2958976a237c26d2E1986b55A9b);

    enum State { Created, Cancelled }

    uint public id = 0;
    struct OrderMesaage {
        string hash;
        address seller;
        address[] buyers;
        uint price;
        State state;
    }

    mapping(uint => OrderMesaage) public orders;


    modifier onlySeller(uint _uid) {
        require(msg.sender == orders[_uid].seller);
        _;
    }

    modifier inState(uint _uid, State _state) {
        require(orders[_uid].state == _state );
        _;
    }

    event PostOrder(uint _uid, string _hash, address seller);

    //卖家生成卖单。
    function postOrder(string memory _hash, uint _price) 
        public 
    {       
        id += 1;
        orders[id].hash = _hash;
        orders[id].seller = msg.sender;
        orders[id].price = _price;

        emit PostOrder(id, _hash, msg.sender);
    }

    //卖家取消卖单。
    function cancerOrder(uint _uid)
        public
        onlySeller(_uid)
        inState(_uid, State.Created)
    {
        orders[_uid].state = State.Cancelled;
    }

    //获取卖单信息
    function getOrder(uint _uid) 
        public 
        view 
        returns (OrderMesaage memory) 
    {
        return (orders[_uid]);
    }


    // 买家确认购买
    function confirmPurchase(uint _uid)
        public
        inState(_uid, State.Created)
    {
        orders[_uid].buyers.push(msg.sender);
        token.transferFromOrigin(orders[_uid].seller, orders[_uid].price);
    }

}
