pragma solidity >=0.4.22 <0.7.0;
pragma experimental ABIEncoderV2;

interface TokenProxyToPurchase  {
    function transferFromProxy(address recipient, uint256 amount) external returns (bool);
}

contract Purchase {
    TokenProxyToPurchase tokenproxy;

    constructor(address proxy) public 
    {
       tokenproxy = TokenProxyToPurchase(proxy);
    }

    enum State { Created, Cancelled }

    uint24 public id = 0;
    uint8 fee = 5;
    address feeTo = 0x9fc10900cF34636330D05e9c6CA976b918627023;
    struct OrderMesaage {
        string hash;
        address seller;
        address[] buyers;
        uint price;
        State state;
    }

    mapping(uint24 => OrderMesaage) public orders;

    modifier onlySeller(uint24 _uid) {
        require(msg.sender == orders[_uid].seller);
        _;
    }

    modifier inState(uint24 _uid, State _state) {
        require(orders[_uid].state == _state );
        _;
    }

    event PostOrder(uint24 indexed uid, address indexed seller, string hash, uint price);
    event CancerOrder(uint24 indexed uid, address indexed seller, string hash);
    event ConfirmPurchase(uint24 indexed uid, address indexed buyer, string hash, uint price);

    //卖家生成卖单。
    function postOrder(string memory _hash, uint _price) 
        public 
    {       
        id += 1;
        orders[id].hash = _hash;
        orders[id].seller = msg.sender;
        orders[id].price = _price;
        emit PostOrder(id, msg.sender, _hash, _price);
    }

    //卖家取消卖单。
    function cancerOrder(uint24 _uid)
        public
        onlySeller(_uid)
        inState(_uid, State.Created)
    {
        orders[_uid].state = State.Cancelled;
        emit CancerOrder(_uid, msg.sender, orders[_uid].hash);
    }

    //获取卖单信息
    function getOrder(uint24 _uid) 
        public 
        view 
        returns (OrderMesaage memory) 
    {
        return (orders[_uid]);
    }


    // 买家确认购买
    function confirmPurchase(uint24 _uid)
        public
        inState(_uid, State.Created)
    {
        uint feeAmount = orders[_uid].price * 5 / 1000;
        orders[_uid].buyers.push(msg.sender);
        tokenproxy.transferFromProxy(feeTo, feeAmount);
        tokenproxy.transferFromProxy(orders[_uid].seller, orders[_uid].price - feeAmount);
        emit ConfirmPurchase(_uid, msg.sender, orders[_uid].hash, orders[_uid].price);
    }

}
